import React from "react";
import axios from "axios";
import "../Contactpage.css";

// create Contact class
class GetContact extends React.Component {
  state = {
    user_name: "",
    message: ""
  };

  updateUsername = e => {
    this.setState({ user_name: e.target.value });
  };

  updateMessage = e => {
    this.setState({ message: e.target.value });
  };

  createContact = () => {
    // Used axios to call the api endpoint and then just retrieved the data from above to create a new contact
    axios({
      method: "post",
      url: "http://localhost:8000/api/newcontact/",
      data: {
        firstname: this.state.firstname,
        lastname: this.state.lastname,
        email: this.state.email,
        user_name: this.state.user_name,
        message: this.state.message
      }
    })
      .then(res => {
        console.log("Passing");
      })
      .catch(err => {
        console.log(err);
      });
  };

  render() {
    return (
      <div className="commentSection">
        <h2 className="user_name">{user_name}</h2>
        <p className="message">{message}</p>
        <button
          onClick={this.updateMessage}
          type="button"
          className="messageBtn"
          id="btn"
        >
          Submit
        </button>
      </div>
    );
  }
}

export default GetContact;

