import React from "react";
import axios from "axios";

// the point of this GetContact component is so that we don't have to hard code the comments section
class EditComment extends React.Component {
  state = {
    firstname: "",
    lastname: "",
    email: "",
    user_name: "",
    message: "",
    contact_id: "",
    old_message: ""
  };

  updateMessage = () => {
    const url = "http://localhost:8000/api/updatecontact";
    let message = "hello";
    let contact_id = 10;
    fetch(url, {
      method: "POST",
      mode: "no-cors",
      headers: {
        Accept: "application/json",
        "Content-Type": "application/json"
      },
      body: JSON.stringify({ message: message, contact_id: contact_id })
    })
      .then(data => {
        console.log("Passing");
      })
      .catch(error => {
        console.log("BAD", error);
      });
  };

  deleteContact = () => {
    const { contact_id } = this.props;
    // Used axios to call the api endpoint and then just retrieved the data from above to create a new contact
    axios
      .delete("http://localhost:8000/api/deletecontact", {
        data: { contact_id: contact_id }
      })
      .then(res => {
        console.log("Passing");
      })
      .catch(err => {
        console.log(err);
      });
  };

  render() {
    return (
<div className="container">
  <h1>COMMENT BOX</h1>
  {/*-----Wrap----------*/}
  <div id="wrap">
    <div id="main">
      <div className="row">
        <div className="col-md-5">
          <h3 className="heading">Comments and Responses</h3>
        </div>
        <div className="col-md-7">
          <div id="upper_blank" />
        </div>
      </div>
    </div>
    <p>Your email address will not be published. Required fields are marked *</p>
    {/*----------Form Start--------*/}
    <div id="form">
      <div className="row">
        <div className="col-md-12">
          <form action method="POST" id="commentform">
            <div id="comment-name" className="form-row">
              <input type="text" placeholder="Name (required)" name="dname" id="name" />
            </div>
            <div id="comment-email" className="form-row">
              <input type="text" placeholder="Mail (will not be published) (required)" name="demail" id="email" />
            </div>
            <div id="comment-url" className="form-row">
              <input type="text" placeholder="Website" name="url" id="url" />
            </div>
            <div id="comment-message" className="form-row">
              <textarea name="comment" placeholder="Message" id="comment" defaultValue={""} />
            </div>
            <a href="#"><input type="submit" name="dsubmit" id="commentSubmit" defaultValue="Submit Comment" /></a>
            <input style={{width: '30px'}} type="checkbox" defaultValue={1} name="subscribe" id="subscribe" defaultChecked="checked" />
            <p1><b>Notify me when new comments are added.</b></p1>
          </form>
        </div>
      </div>
    </div>
  </div>
  {/*-----------------Reply Section-----*/}
  <div id="second">
    <div className="row">
      <div className="col-md-2">
        <h3 className="second_heading"><b>Leave a Reply</b></h3>
      </div>
      <div className="col-md-10">
        <div className="blank" />
      </div>
    </div>
  </div>
  <div id="middle">
    <form>
      <a href="#"><input type="text" defaultValue="reply" name="dreply" id="inner_reply" /></a>
    </form>
  </div>
</div>

