import React from "react";
import axios from "axios";


class DeleteContact extends React.Component {
  state = {
    firstname: "",
    lastname: "",
    email: "",
    user_name: "",
    message: ""
  };
  // created update functions for each field I have
  deleteFirstname = e => {
    this.setState({ firstname: e.target.value });
  };

  deleteLastname = e => {
    this.setState({ lastname: e.target.value });
  };

  deleteEmail = e => {
    this.setState({ email: e.target.value });
  };

  deleteUsername = e => {
    this.setState({ user_name: e.target.value });
  };

  deleteMessage = e => {
    this.setState({ message: e.target.value });
  };

  deleteContact = () => {
    // Used axios to call the api endpoint and then just retrieved the data from above to create a new contact
    axios
      .post("http://localhost:8000/api/deletecontact", {
        firstname: this.state.firstname,
        lastname: this.state.lastname,
        email: this.state.email,
        user_name: this.state.user_name,
        message: this.state.message
      })
      .then(res => {
        console.log("Passing");
      })
      .catch(err => {
        console.log(err);
      });
  };
  GetContact = ({ user_name, message }) => {
    return (
      <div className="commentSection">
        <h2 className="userName">{user_name}</h2>
        <p className="commentMessage">{message}</p>
        <button>Edit</button>
        <button>Delete</button>
      </div>
    );
  };
}

export default DeleteContact;
