  updateMessage = () => {
    const url = "http://localhost:8000/api/updatecontact";
    let message = "hello";
    let contact_id = 10;
    fetch(url, {
      method: "POST",
      mode: "no-cors",
      headers: {
        Accept: "application/json",
        "Content-Type": "application/json"
      },
      body: JSON.stringify({ message: message, contact_id: contact_id })
    })
      .then(data => {
        console.log("Passing");
