import React from "react";
import axios from "axios";
import GetContact from "./GetContact";

class CommentBox extends React.Component {
  state = {
    firstname: "",
    lastname: "",
    email: "",
    user_name: "",
    message: ""
  }
  // created update functions for each field I have
  deleteFirstname = (e) => {
    this.setState({ firstname: e.target.value });
  }

  deleteLastname = (e) => {
    this.setState({ lastname: e.target.value });
  }

  deleteEmail = (e) => {
    this.setState({ email: e.target.value });
  }

  deleteUsername = (e) => {
    this.setState({ user_name: e.target.value });
  }

  deleteMessage = (e) => {
    this.setState({ message: e.target.value });
  }

  deleteContact = () => {
    // Used axios to call the api endpoint and then just retrieved the data from above to create a new contact
    axios.delete('http://localhost:8000/api/newcontact', {
       firstname:this.state.firstname,
       lastname:this.state.lastname,
       email:this.state.email,
       user_name:this.state.user_name,
       message:this.state.message
    })
    .then(res => {
      console.log("Passing");
    })
    .catch(err => {
      console.log(err);
    });;
  }

  render() {
    return (
      <div className="getContacts">
        {this.state.contacts &&
          this.state.contacts.map(contacts => (
            <GetContact
              user_name={contacts.user_name}
              message={contacts.message}
            />
          ))}
      </div>
    );
  }
}

export default CommentBox;
